        private void button7_Click(object sender, EventArgs e)
        {
            String s = "[";
            bool first = true;
            foreach (var pt in render.points)
            {
                if (first)
                {
                    first = false;
                }
                else
                {
                    s += ", ";
                }
                s += pt.segid.ToString();

            }
            s += "]";
            textBox3.Text = s;

            s = "[";
            first = true;

            foreach (var pt in render.points)
            {
                if (first)
                {
                    first = false;
                }
                else
                {
                    s += ", ";
                }
                s += "[" + pt.x.ToString() + "," + pt.y.ToString() + "]";

            }
            s += "]";
            textBox4.Text = s;
        }

